import React from 'react';
import { Layout, Header, Navigation, Drawer, Content } from 'react-mdl';
import './App.css';
import Home from './components/home';
import {Link} from 'react-router-dom';
import './index.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      style:null,
    }
  }

  toggle(position){
     if(this.state.style === position){
       this.setState({style:''});
     }
     else{
      this.setState({style:position});
     }
  }

  myStyle(position){
    if(this.state.style === position){
      return "#ff6406";
    }
    else{
      return "";
    }
  }

  render(){   
  return (    
    <div className="demo-big-content" >
      <Layout className="header-gradient" style={{position:''}}>
        <Header className="header-gradient color" style={{backgroundColor:'rgba(50, 50, 50, 0.5)', position:'fixed'}} title={<a style={{textDecoration:'none', color:'#ff6406'}} href='/'>Ankit Bansal</a >} scroll>
            <Navigation> 
                <a href="#about"  style={{color: this.myStyle(0),fontSize:'20px'}} onClick={()=>this.toggle(0)}>About</a>
                <a href="#projects" style={{color: this.myStyle(1),fontSize:'20px' }} onClick={()=>this.toggle(1)}>Projects</a >
                <a href="#resume" style={{color: this.myStyle(2),fontSize:'20px'}} onClick={()=>this.toggle(2)}>Resume</a >
                <a href="#contacts" style={{color: this.myStyle(3),fontSize:'20px'}} onClick={()=>this.toggle(3)}>Contacts</a >
            </Navigation>
        </Header>
        <Drawer >
            <Navigation>
                <a href="/" style={{color: this.myStyle(4),fontSize:'20px'}} onClick={()=>this.toggle(4)}>Home</a >
                <a href="#about" style={{color: this.myStyle(0),fontSize:'20px'}} onClick={()=>this.toggle(0)}>About</a >
                <a href="#projects" style={{color: this.myStyle(1),fontSize:'20px' }} onClick={()=>this.toggle(1)}>Projects</a >
                <a href="#resume" style={{color: this.myStyle(2),fontSize:'20px'}} onClick={()=>this.toggle(2)}>Resume</a >
                <a href="#contacts"  style={{color: this.myStyle(3),fontSize:'20px'}} onClick={()=>this.toggle(3)}>Contacts</a >
            </Navigation>
        </Drawer>
        <Content >  
            <Home />
        </Content>    
      </Layout>   
    </div>
  );
  }
}

export default App;
